mapper.insert:
INSERT INTO CART (USER_ID, ITEM_ID, AMOUNT)
VALUES (#{userId}, #{itemId}, #{amount})
ON DUPLICATE KEY UPDATE AMOUNT = AMOUNT + #{amount}

mapper.update:
UPDATE CART
SET AMOUNT = #{amount}
WHERE USER_ID = #{userId} AND ITEM_ID = #{itemId}

mapper.select:
SELECT ADD_ID, ADDRESS, ADD_NAME, USER_ID
FROM ADDRESS
WHERE ADD_ID = #{addressId} AND USER_ID = #{userId}

mapper.update:
UPDATE ADDRESS
SET ADDRESS = #{address}, ADD_NAME = #{addName}
WHERE ADD_ID = #{addId} AND USER_ID = #{userId}

mapper.select:
SELECT ADD_ID, ADDRESS, ADD_NAME, USER_ID
FROM ADDRESS
WHERE USER_ID = #{userId}
