mapper.insert:
INSERT INTO CART (USER_ID, ITEM_ID, AMOUNT)
VALUES (#{userId}, #{itemId}, #{amount})
ON DUPLICATE KEY UPDATE AMOUNT = AMOUNT + #{amount}

mapper.update:
UPDATE CART
SET AMOUNT = #{amount}
WHERE USER_ID = #{userId} AND ITEM_ID = #{itemId}

UPDATE INVENTORY i
JOIN SHOP s ON i.STOR_ID = s.STOR_ID
SET i.AMOUNT = i.AMOUNT - #{param2}
WHERE s.ITEM_ID = #{param1}

mapper.select:
SELECT ADD_ID, ADDRESS, ADD_NAME, USER_ID
FROM ADDRESS
WHERE ADD_ID = #{addId} AND USER_ID = #{userId}

mapper.update:
